import React, { useEffect } from 'react';
import { useQuery } from '@apollo/client';

import ProductItem from '../ProductItem';
import { QUERY_PRODUCTS } from '../../utils/queries';
import spinner from '../../assets/spinner.gif';

// import { useStoreContext } from '../../utils/GlobalState';
import { UPDATE_PRODUCTS } from '../../utils/actions';

import { idbPromise } from "../../utils/helpers";

// REDUX IMPORTS
import { useSelector, useDispatch } from 'react-redux'
import store from '../../utils/Redux/store';

function ProductList() {
  // const [state, dispatch] = useStoreContext();

  // const { currentCategory } = state;

  const { loading, data } = useQuery(QUERY_PRODUCTS);

  // REDUX VARIABLES
  const rState = store.getState();
  const rCurrentCategory = useSelector(rState => rState.currentCategory)
  const rProducts = useSelector(rState => rState.products)
  const rDispatch = useDispatch();
  // console.log('redux state: ', rState)

  useEffect(() => {
    if (data) {
      // dispatch({
      //   type: UPDATE_PRODUCTS,
      //   products: data.products
      // });

      // REDUX DISPATCH
      rDispatch({
        type: UPDATE_PRODUCTS,
        products: data.products
      });

      data.products.forEach((product) => {
        idbPromise('products', 'put', product);
      });
      // add else if to check if `loading` is undefined in `useQuery()` Hook
    } else if (!loading) {
      // since we're offline, get all of the data from the `products` store
      idbPromise('products', 'get').then((products) => {
        // use retrieved data to set global state for offline browsing
        // dispatch({
        //   type: UPDATE_PRODUCTS,
        //   products: products
        // });

        // REDUX
        rDispatch({
          type: UPDATE_PRODUCTS,
          products: products
        });
      });
    }
  }, [data, loading, rDispatch]);

  function filterProducts() {
    if (!rCurrentCategory) {
      return rProducts;
    }

    return rProducts.filter(product => product.category._id === rCurrentCategory);
  }

  return (
    <div className="my-2">
      <h2>Our Products:</h2>
      {rProducts.length ? (
        <div className="flex-row">
          {filterProducts().map((product) => (
            <ProductItem
              key={product._id}
              _id={product._id}
              image={product.image}
              name={product.name}
              price={product.price}
              quantity={product.quantity}
            />
          ))}
        </div>
      ) : (
        <h3>You haven't added any products yet!</h3>
      )}
      {loading ? <img src={spinner} alt="loading" /> : null}
    </div>
  );
}

export default ProductList;
